import React from 'react'
import {useState} from 'react'
import './TodoList.css'
import ClearIcon from '@material-ui/icons/Clear';

function TodoList() {
    const [todo, setTodo] = useState('')

    const handleChange = (event) => {
        setTodo(event.target.value)
    }

    const [inputs, setInputs] = useState([])

    const handleKeyPress = (e) => {
        e.preventDefault()

        if (todo !== ""){

            const newTodo = {description: todo, id: Math.floor(Math.random * 10000)}

            setInputs([...inputs, newTodo])
            setTodo('')
            console.log(newTodo)}
    }
    const handleDelete = (id) => {
        let newTodos = inputs.filter((todo) => todo.id !== id)
        setInputs([...newTodos])


    }
    const handleDeleteAll= () => {
        setInputs([])
    }

    return (
        <div className="todolist-app" >
        <div className="input">
            <input type='text' value={todo} onChange={handleChange}  placeholder="Add a todo"/> <button className="add-btn" onClick={handleKeyPress}>Add task</button>
        </div>
        <div className="todolist">
            {inputs.map((todo, index) => {return <div className="todo" key={index} ><p className="todo-text">{todo}</p> <ClearIcon className= "clearicon" onClick={handleDelete}/></div>})}

        </div>
        <button className="clear-btn" onClick={handleDeleteAll}>Clear all</button>



        </div>
    )
}
export default TodoList
