import React from 'react'
import {useState} from 'react'
import './TodoList.css'
import ClearIcon from '@material-ui/icons/Clear';

function TodoList() {
    const [value, setValue] = useState('')

    const handleChange = (event) => {
        setValue(event.target.value)
    }

    const [inputs, setInputs] = useState([])

    const handleKeyPress = () => {

        if (value !== ""){

            let newTodo = {description: value, id: new Date().getTime()}

            setInputs([...inputs, value])
            setValue('')
            console.log(newTodo)
    }}
    const handleDelete = (e, id) => {
        e.preventDefault()
        let newTodos = [...inputs].filter((value) => value.id !== id)
        setInputs([...newTodos])


    }
    const handleDeleteAll= () => {
        setInputs([])
    }
    const displayTodos = inputs.length > 1

    return (
        <div className="todolist-app" >
        <div className="input">
            <input type='text' value={value} onChange={handleChange}  placeholder="Add a todo"/> <button className="add-btn" onClick={handleKeyPress}>Add task</button>
        </div>
        {/* <div className="todolist">
            {inputs.map((value, index) => {return <div className="todo" key={index} ><p className="todo-text">{value}</p> <ClearIcon className= "clearicon" onClick={handleDelete} /></div>})}

        </div> */}
        { displayTodos && <button className="clear-btn" onClick={handleDeleteAll}>Clear all</button> && <div className="todolist">
            {inputs.map((value, index) => {return <div className="todo" key={index} ><p className="todo-text">{value}</p> <ClearIcon className= "clearicon" onClick={handleDelete} /></div>})}

        </div> }



        </div>
    )
}
export default TodoList
