import React from 'react'
import {useState} from 'react'
import './TodoList.css'
import ClearIcon from '@material-ui/icons/Clear';

function TodoList() {
    const [value, setValue] = useState('')
    const [view, setView] = useState(true)

    const handleChange = (event) => {
        setValue(event.target.value)
    }

    const [inputs, setInputs] = useState([])
    const newTodo = {"description": {value}, "id": Math.floor(Math.random * 10000)}

    const handleKeyPress = () => {

            setInputs([...inputs, newTodo])
            setValue('')
            console.log(newTodo)



    }
    const handleDelete = () => {
        setView(!view)
    }

    return (
        <div className="todolist-app" >
        <div className="input">
            <input type='text' value={value} onChange={handleChange}  placeholder="Add a todo"/> <button className="add-btn" onClick={handleKeyPress}>Add task</button>
        </div>
        <div className="todolist">
            {inputs.map((value) => {return <div className="todo" ><p className="todo-text">{value}</p> <ClearIcon className= "clearicon" onClick={handleDelete}/></div>})}
            <button className="clear-btn" >Clear all</button>
        </div>



        </div>
    )
}

export default TodoList
